mapper.select:
SELECT A.BOARD_NO, BOARD_TYPE, BOARD_TITLE, BOARD_DATE, WRITER , BOARD_TITLE, BOARD_HIT, BOARD_CONTENT, A.LECTURE_CODE, A.ATTEND_NO , B.ATTACHMENT_NO, FILE_NAME, FILE_TYPE , FILE_SIZE, FILE_PATH, FILE_ORDER FROM BOARD A LEFT OUTER JOIN ATTACHMENT B ON(A.BOARD_NO = B.BOARD_NO) WHERE A.BOARD_NO IN( TO_NUMBER(#{board_no}),
(SELECT MAX(BOARD_NO) FROM BOARD WHERE BOARD_NO < TO_NUMBER(#{board_no})), (SELECT MIN(BOARD_NO) FROM BOARD WHERE BOARD_NO > TO_NUMBER(#{board_no}))
) ORDER BY TO_NUMBER(A.BOARD_NO)

mapper.update:
UPDATE BOARD
SET BOARD_HIT = TO_NUMBER(BOARD_HIT) + 1
WHERE BOARD_NO = #{board_no}

mapper.insert:

			SELECT TO_CHAR(BO_SEQ.NEXTVAL) FROM DUAL

		INSERT INTO BOARD(
			BOARD_NO, BOARD_TYPE, BOARD_ATTACHMENTCOUNT, BOARD_DATE,
            BOARD_TITLE, BOARD_HIT, WRITER, LECTURE_CODE, BOARD_CONTENT, ATTEND_NO
		)VALUES(
			#{board_no}, #{board_type}, #{board_attachmentcount}, SYSDATE
			, #{board_title}, '0', #{writer}, #{lecture_code}, #{board_content}, #{attend_no, jdbcType=VARCHAR}
		)

mapper.update:
UPDATE BOARD
SET
BOARD_TITLE = #{board_title}
, BOARD_DATE = SYSDATE
, BOARD_CONTENT = #{board_content}
WHERE BOARD_NO = #{board_no}
